import './App.css';
import React, {useState} from "react";
import { useForm } from "react-hook-form";

function Test() {
  const { register, handleSubmit, watch, errors } = useForm();
  const onSubmit = data => console.log(data);
  return (
    <div className="App">
      <form onSubmit={handleSubmit(onSubmit)}>
        <input name="requiredField" ref={register({ required: true })} />
        <br />
        {errors.requiredField && <span>This field is required</span>}
        <br />
        <input type="submit" />
      </form>
    </div>
  );
}

function App() {
  const handleSubmit = event => {
    event.preventDefault();
    alert('สมัครสมาชิกเรียบร้อย')
  }

  return (
    <div className="App">
      <form onSubmit={handleSubmit}>
        <fieldset>
          <legend>สมัครสมาชิก</legend>
          <div className="name">
            <label>ชื่อ-นามสกุล : </label>
            <input type="text" placeholder="กรุณากรอกชื่อ นามสกุล" required></input>
          </div>
          <div className="gender">
            <label>เพศ : </label>
            <select classname="gender">
              <option value="">เลือกเพศ</option>
              <option value="">หญิง</option>
              <option value="">ชาย</option>
              <option value="">อื่นๆ</option>
            </select>
          </div>
          <div className="age">
            <label>อายุ : </label>
            <input type="number" required></input>
          </div>
          <div className="birth">
            <label>วัน/เดือน/ปี : </label>
            <input type="date" required></input>
          </div>
          <div className="phone">
            <label>เบอร์โทรศัพท์ : </label>
            <input type="tel" placeholder="0xx-xxxxxxx" required></input>
          </div>
          <div className="mail">
            <label>อีเมล์ : </label>
            <input type="email" placeholder="email@xxx.com" required></input>
          </div>
        </fieldset>
        <button>Submit</button>
      </form>
    </div>
  );
}


export default App;
