import React, {useState} from 'react';
import moment from 'moment';

function App() {
  const [nameUser, setName] = useState('');
  const [age, setAge] = useState('');
  const [date, setDate] = useState('');
  const [phone, setPhone] = useState('');
  const [email, setEmail] = useState('');
  const [errorMessageName, setErrorMessageName] = useState('');
  const [errorMessageAge, setErrorMessageAge] = useState('');
  const [errorMessageDate, setErrorMessageDate] = useState('');
  const [errorMessagePhone, setErrorMessagePhone] = useState('');
  const [errorMessageEmail, setErrorMessageEmail] = useState('');


  function handleSubmit(e) {
    e.preventDefault();

      // NAME
    const regName = /^[a-zA-Z]+\s+[a-zA-Z]+$/;
    const checkName = regName.test(nameUser);
    if (!checkName) {
      setErrorMessageName('*กรอกชื่อ นามสกุลให้ถูกต้อง');
    } else {
      setErrorMessageName('');
    }
    if (nameUser == 0) {
      setErrorMessageName('*กรอกชื่อ นามสกุล');
    }


      // AGE
    const regAge = /^[0-9]+$/;
    const checkAge = regAge.test(age);
    if (!checkAge) {
      setErrorMessageAge('*กรอกเป็นตัวเลข')
    } else {
      setErrorMessageAge('')
    }
    if (age > 50) {
      setErrorMessageAge('*อายุเกินเกณฑ์')
    }
    else if (age < 10 && age > 0) {
      setErrorMessageAge('*อายุต่ำกว่าเกณฑ์')
    }
    else if (age == 0) {
      setErrorMessageAge('*กรอกอายุ')
    }

      // DATE
    const epochtime = Date.now();
    const OneYear = 31556926;
    const unixTimestamp = moment(date, 'YYYY-MM-DD').unix();
    const getEpochtime = (epochtime/1000) - unixTimestamp;
    const getYear = getEpochtime /  OneYear;
    const years = Math.trunc(getYear);

    if (years != age) {
      setErrorMessageDate('*วัน-เดือน-ปี ไม่สัมพันธ์กับอายุ');
    }
    else if (getYear == 0) {
      setErrorMessageDate('*กรอกวัน-เดือน-ปี')
    }
    else {
      setErrorMessageDate('')
    }

    // PHONE
    const regPhone = /^[0-9]{3}-[0-9]{7}/;
    const checkPhone = regPhone.test(phone);
    if (!checkPhone) {
        setErrorMessagePhone('*กรอก 0xx-xxxxxxx');
      }else{
        setErrorMessagePhone('');
      }
      if (phone == 0) {
        setErrorMessagePhone('*กรอกเบอร์โทรศัพท์');
      }


      // EMAIL
    const regMail = /^[a-zA-z0-9]+@[a-z]+.[a-z]+.[a-z]+.[a-z]+$/;
    const checkMail = regMail.test(email);
    if (!checkMail) {
      setErrorMessageEmail('*กรอก email@xxx.xxx');
    }else{
        setErrorMessageEmail('');
    }
    if (email == 0) {
      setErrorMessageEmail('*กรอกemail');
    }

      //SUBMIT
    if (!checkName || !checkAge || !checkPhone || !checkMail || years != age) {
      alert('กรอกข้อมูลไม่ถูกต้อง');
    }
    else {
      alert('ลงทะเบียนเรียบร้อย');
    }
  }

  return (
    <div className="App">
      <form onSubmit={handleSubmit}>
        <fieldset>
          <legend>Register</legend>
          <div className="name">
            <label>Fullname : </label>
            <input type="text" placeholder="Firstname - Lastname"
            onChange={e => setName(e.target.value)}></input>
            {errorMessageName && <div className="error">{errorMessageName}</div>}
          </div>
          <div className="gender">
            <label>Gender : </label>
            <select className="gender">
              <option value="">Choose Gender</option>
              <option value="">Female</option>
              <option value="">Male</option>
              <option value="">Other</option>
            </select>
          </div>
          <div className="age">
            <label>Age : </label>
            <input type="text" maxLength="2" onChange={e => setAge(e.target.value)}></input>
            {errorMessageAge && <div className="error"> {errorMessageAge}</div>}
          </div>
          <div className="date">
            <label>Date Of Birth : </label>
            <input type="date" onChange={e => setDate(e.target.value)}></input>
            {errorMessageDate && <div className="error">{errorMessageDate}</div>}
          </div>
          <div className="phone">
            <label>Phone : </label>
            <input type="tel" placeholder="0xx-xxxxxxx" maxLength="11"
            onChange={e => setPhone(e.target.value)} ></input>
            {errorMessagePhone && <div className="error">{errorMessagePhone}</div>}
          </div>
          <div className="mail">
            <label>Email : </label>
            <input type="text" placeholder="email@xxx.com" 
            onChange={e => setEmail(e.target.value)}></input>
            {errorMessageEmail && <div className="error">{errorMessageEmail}</div>}
          </div>
        </fieldset>
        <button>ลงทะเบียน</button>
      </form>
    </div>
  );
}

export default App;
