import React, {useState} from 'react';
import moment from 'moment';


function App() {
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [date, setDate] = useState('');
  const [phone, setPhone] = useState('');
  const [email, setEmail] = useState('');

  function handleSubmit(e) {
    e.preventDefault();
    if (age > 50) {
      alert('อายุเกินเกณฑ์')
    }
    else if (age < 10) {
      alert('อายุต่ำกว่าเกณฑ์')
    }

    const epochtime = Date.now();
    const OneYear = 31556926;
    const unixTimestamp = moment(date, 'YYYY-MM-DD').unix();
    const getEpochtime = (epochtime/1000) - unixTimestamp;
    const getYear = getEpochtime /  OneYear;
    console.log(epochtime + ' - ' + unixTimestamp + ' = ' + getEpochtime);
    console.log(getYear);
    // if (getYear >= age || getYear <= age) {
    //   alert('วัน-เดือน-ปี ไม่สัมพันธ์กับอายุ');
    // }

    const phoneFormat = (setPhone) => {
      if(!setPhone || isNaN(setPhone)) return `input must be a number was sent ${setPhone}`
      if(typeof(setPhone) !== 'string') setPhone = setPhone.toString()
      if(phosetPhonene.length === 11){
        alert (setPhone.replace(/(\d{3})(\d{3})(\d{4})/, "$1-$2$3"));
      } else if(setPhone.length < 10) {

        alert ('was not supplied enough numbers please pass a 10 digit number')
      } else if(setPhone.length > 10) {

        alert ('was supplied too many numbers please pass a 10 digit number')
      }else{

        alert ('something went wrong')
      }
    }
  }

  return (
    <div className="App">
      <form onSubmit={handleSubmit}>
        <fieldset>
          <legend>สมัครสมาชิก</legend>
          <div className="name">
            <label>ชื่อ-นามสกุล : </label>
            <input type="text" placeholder="กรุณากรอกชื่อ นามสกุล" 
            onChange={e => setName(e.target.value)}></input>
          </div>
          <div className="gender">
            <label>เพศ : </label>
            <select className="gender">
              <option value="">เลือกเพศ</option>
              <option value="">หญิง</option>
              <option value="">ชาย</option>
              <option value="">อื่นๆ</option>
            </select>
          </div>
          <div className="age">
            <label>อายุ : </label>
            <input type="number" onChange={e => setAge(e.target.value)}></input>
          </div>
          <div className="date">
            <label>วัน/เดือน/ปี : </label>
            <input type="date" onChange={e => setDate(e.target.value)}></input>
          </div>
          <div className="phone">
            <label>เบอร์โทรศัพท์ : </label>
            <input type="tel" placeholder="0xx-xxxxxxx" maxLength="11"
            onChange={e => setPhone(e.target.value)}></input>
          </div>
          <div className="mail">
            <label>อีเมล์ : </label>
            <input type="email" placeholder="email@xxx.com" 
            onChange={e => setEmail(e.target.value)}></input>
          </div>
        </fieldset>
        <button>Submit</button>
      </form>
    </div>
  );
}

export default App;
