import React, {useState} from 'react';
import moment from 'moment';

function App() {
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [date, setDate] = useState('');
  const [phone, setPhone] = useState('');
  const [email, setEmail] = useState('');
  const [errorMessageName, setErrorMessageName] = useState('');
  const [errorMessageAge, setErrorMessageAge] = useState('');
  const [errorMessageDate, setErrorMessageDate] = useState('');
  const [errorMessagePhone, setErrorMessagePhone] = useState('');
  const [errorMessageEmail, setErrorMessageEmail] = useState('');

  function handleSubmit(e) {
    setErrorMessageName(' ');
    setErrorMessageAge('');
    setErrorMessageDate('');
    setErrorMessagePhone('');
    setErrorMessageEmail('');

    e.preventDefault();

      // NAME
    if (name != -1) {
      setErrorMessageName('*กรอกชื่อ นามสกุล');
    }

      // AGE
    if (age > 50) {
      setErrorMessageAge('*อายุเกินเกณฑ์')
    }
    else if (age < 10 && age > 0) {
      setErrorMessageAge('*อายุต่ำกว่าเกณฑ์')
    }
    else if (age == 0) {
      setErrorMessageAge('*กรอกอายุ')
    }

      // DATE
    const epochtime = Date.now();
    const OneYear = 31556926;
    const unixTimestamp = moment(date, 'YYYY-MM-DD').unix();
    const getEpochtime = (epochtime/1000) - unixTimestamp;
    const getYear = getEpochtime /  OneYear;
            // console.log((epochtime/1000) + ' - ' + unixTimestamp + ' = ' + getEpochtime);
            // console.log(getYear + ' ---- ' + age);
    if (Math.trunc(getYear) != age) {
      setErrorMessage('*วัน-เดือน-ปี ไม่สัมพันธ์กับอายุ');
    }
    else if (getYear == 0) {
      setErrorMessageAge('*กรอกวัน-เดือน-ปี')
    }


  }

  return (
    <div className="App">
      <form onSubmit={handleSubmit}>
        <fieldset>
          <legend>สมัครสมาชิก</legend>
          <div className="name">
            <label>ชื่อ-นามสกุล : </label>
            <input type="text" placeholder="กรุณากรอกชื่อ นามสกุล" 
            onChange={e => setName(e.target.value)}></input>
            {errorMessageName && <div className="error">{errorMessageName}</div>}
          </div>
          <div className="gender">
            <label>เพศ : </label>
            <select className="gender">
              <option value="">เลือกเพศ</option>
              <option value="">หญิง</option>
              <option value="">ชาย</option>
              <option value="">อื่นๆ</option>
            </select>
          </div>
          <div className="age">
            <label>อายุ : </label>
            <input type="number" onChange={e => setAge(e.target.value)}></input>
            {errorMessageAge && <div className="error"> {errorMessageAge}</div>}
          </div>
          <div className="date">
            <label>วัน/เดือน/ปี : </label>
            <input type="date" onChange={e => setDate(e.target.value)}></input>
            {errorMessage && <div className="error"> {errorMessage}</div>}
          </div>
          <div className="phone">
            <label>เบอร์โทรศัพท์ : </label>
            <input type="tel" placeholder="0xx-xxxxxxx" maxLength="11"
            onChange={e => setPhone(e.target.value)}></input>
            {errorMessage && <div className="error">*กรอกเบอร์โทรศัพท์</div>}
          </div>
          <div className="mail">
            <label>อีเมล์ : </label>
            <input type="email" placeholder="email@xxx.com" 
            onChange={e => setEmail(e.target.value)}></input>
            {errorMessage && <div className="error">*กรอกemail</div>}
          </div>
        </fieldset>
        <button>Submit</button>
      </form>
    </div>
  );
}

export default App;
